import React from "react";
import styled from "styled-components";
import CodeBox from "./CodeBox";
import { useState } from "react";

function PostList() {
  //   const [codeText, setCodeText] = useState("Test");
  //   const [codeLanguage, setCodeLanguage] = useState("javascript");
  //   const [showLineNum, setShowLineNum] = useState(1);
  //   const [startingLineNum, setStartingLineNum] = useState(1);
  //   const inputCode = React.useRef(null);

  //   const handleChange = (e) => {
  //     setCodeText(e.target.value);
  //   };

  //   return (
  //     <PostListContainer>
  //       <InputCodeText type="text" onChange={handleChange} />
  //       <CodeBox
  //         code={codeText}
  //         language={codeLanguage}
  //         showLineNumbers={showLineNum}
  //         startingLineNumber={startingLineNum}
  //         wrapLines
  //       />
  //     </PostListContainer>
  //   );
  // }

  //  이미지 미리보기
  const [imageSrc, setImageSrc] = useState("");

  const encodeFileToBase64 = (fileBlob) => {
    const reader = new FileReader();
    reader.readAsDataURL(fileBlob);
    return new Promise((resolve) => {
      reader.onload = () => {
        setImageSrc(reader.result);
        resolve();
      };
    });
  };

  return (
    <PostListContainer>
      <input
        type="file"
        onChange={(e) => {
          encodeFileToBase64(e.target.files[0]);
        }}
      />
      <ShowImagePreview>
        {imageSrc && <img src={imageSrc} alt="image-preview" />}
      </ShowImagePreview>
    </PostListContainer>
  );
}
export default PostList;

const PostListContainer = styled.div`
  width: 600px;
`;

const InputCodeText = styled.input`
  width: 600px;
  height: 200px;
`;

const ShowImagePreview = styled.div``;
