import React from "react";
import styled from "styled-components";
import Header from "../components/Header";
import { useLocation } from "react-router-dom";
import { useSelector } from "react-redux";

function PostDetail() {
  const posts = useSelector((state) => state.post.list);
  // const updatePosting = () => {
  //   dispatch(updatePostDB());
  // };
  const location = useLocation();
  const data = location.state.data;
  console.log(data.id);

  console.log(posts);

  return (
    <>
      <Header />
      <PostDetailContainer>
        <PostDetailTitle>주제:</PostDetailTitle>
        <PostDetailTitle>내용:</PostDetailTitle>
        <PostDetailImage>이미지:</PostDetailImage>
        <CommentContainer>
          <CommentTop>
            <CommentInput placeholder="댓글을 입력해주세요" />
            <button>수정버튼</button>
            <button>삭제버튼</button>
          </CommentTop>

          <CommentList>댓글 모음 컨테이너</CommentList>
        </CommentContainer>
      </PostDetailContainer>
    </>
  );
}

export default PostDetail;

const PostDetailContainer = styled.div`
  display: flex;
  flex-direction: column;
`;
const PostDetailTitle = styled.div`
  width: 200px;
`;
const PostDetailText = styled.div``;
const PostDetailImage = styled.div``;
const CommentContainer = styled.div``;
const CommentTop = styled.div``;
const CommentInput = styled.input``;
const CommentList = styled.div`
  border: 1px solid black;
`;
